parameters.jsCode:
// Process events and prepare reminders
const events = $input.first().json.events || [];
const now = new Date();
const reminders = [];

for (const event of events) {
  const eventStart = new Date(event.startTime);
  const minutesUntil = Math.floor((eventStart - now) / (1000 * 60));

  // Different reminder windows
  const reminder15 = minutesUntil >= 14 && minutesUntil <= 16;
  const reminder60 = minutesUntil >= 59 && minutesUntil <= 61;
  const reminderDay = minutesUntil >= 1430 && minutesUntil <= 1450; // ~24 hours

  if (reminder15 || reminder60 || reminderDay) {
    let reminderType, timeText;
    if (reminder15) {
      reminderType = '15min';
      timeText = '15 minutes';
    } else if (reminder60) {
      reminderType = '1hour';
      timeText = '1 hour';
    } else {
      reminderType = '1day';
      timeText = 'tomorrow';
    }

    reminders.push({
      eventId: event.id,
      title: event.title,
      description: event.description,
      startTime: event.startTime,
      endTime: event.endTime,
      location: event.location,
      attendees: event.attendees || [],
      organizer: event.organizer,
      reminderType: reminderType,
      timeText: timeText,
      meetingUrl: event.meetingUrl
    });
  }
}

return { reminders, count: reminders.length };

