// Calculate invoice totals
const data = $input.first().json;
const items = data.items || [];
const currency = data.currency || 'ZAR';

let subtotal = 0;
const processedItems = items.map((item, idx) => {
  const qty = parseFloat(item.quantity) || 1;
  const price = parseFloat(item.unitPrice) || 0;
  const discount = parseFloat(item.discount) || 0;
  const lineSubtotal = qty * price;
  const lineDiscount = lineSubtotal * (discount / 100);
  const lineTotal = lineSubtotal - lineDiscount;
  subtotal += lineTotal;

  return {
    lineNumber: idx + 1,
    description: item.description,
    quantity: qty,
    unitPrice: price,
    discount: discount,
    lineTotal: lineTotal
  };
});

const taxRate = parseFloat(data.taxRate) || 15;
const taxAmount = subtotal * (taxRate / 100);
const total = subtotal + taxAmount;

// Generate invoice HTML
const html = `
<!DOCTYPE html>
<html>
<head>
  <style>
    body { font-family: Arial, sans-serif; margin: 40px; color: #333; }
    .header { display: flex; justify-content: space-between; border-bottom: 3px solid #007AFF; padding-bottom: 20px; margin-bottom: 30px; }
    .logo { font-size: 28px; font-weight: bold; color: #007AFF; }
    .invoice-title { text-align: right; }
    .invoice-title h1 { margin: 0; color: #333; font-size: 32px; }
    .invoice-number { color: #666; margin-top: 5px; }
    .parties { display: flex; justify-content: space-between; margin-bottom: 40px; }
    .party { width: 45%; }
    .party-label { font-size: 12px; color: #888; text-transform: uppercase; margin-bottom: 5px; }
    table { width: 100%; border-collapse: collapse; margin-bottom: 30px; }
    th { background: #007AFF; color: white; padding: 12px; text-align: left; }
    td { padding: 12px; border-bottom: 1px solid #eee; }
    .totals { width: 300px; margin-left: auto; }
    .totals td { padding: 8px 12px; }
    .total-row { font-weight: bold; font-size: 18px; background: #f5f5f5; }
    .payment-info { background: #f9f9f9; padding: 20px; border-radius: 8px; margin-top: 30px; }
    .footer { margin-top: 50px; text-align: center; color: #888; font-size: 12px; }
  </style>
</head>
<body>
  <div class="header">
    <div class="logo">${data.businessName || 'Your Company'}</div>
    <div class="invoice-title">
      <h1>INVOICE</h1>
      <div class="invoice-number">${data.invoiceNumber}</div>
    </div>
  </div>

  <div class="parties">
    <div class="party">
      <div class="party-label">From</div>
      <strong>${data.businessName || 'Your Company'}</strong><br>
      ${data.businessAddress || ''}<br>
      ${data.businessEmail || ''}<br>
      VAT: ${data.businessVat || ''}
    </div>
    <div class="party">
      <div class="party-label">Bill To</div>
      <strong>${data.customerName}</strong><br>
      ${data.customerAddress || ''}<br>
      ${data.customerEmail || ''}
    </div>
  </div>

  <table style="width: 50%; margin-bottom: 30px;">
    <tr><td style="background: #f5f5f5; width: 50%;"><strong>Invoice Date:</strong></td><td>${new Date(data.invoiceDate).toLocaleDateString()}</td></tr>
    <tr><td style="background: #f5f5f5;"><strong>Due Date:</strong></td><td>${new Date(data.dueDate).toLocaleDateString()}</td></tr>
    <tr><td style="background: #f5f5f5;"><strong>Payment Terms:</strong></td><td>Net ${data.paymentTermsDays || 30}</td></tr>
  </table>

  <table>
    <thead>
      <tr>
        <th>#</th>
        <th>Description</th>
        <th style="text-align: right;">Qty</th>
        <th style="text-align: right;">Price</th>
        <th style="text-align: right;">Total</th>
      </tr>
    </thead>
    <tbody>
      ${processedItems.map(item => \`
        <tr>
          <td>\${item.lineNumber}</td>
          <td>\${item.description}</td>
          <td style="text-align: right;">\${item.quantity}</td>
          <td style="text-align: right;">\${currency} \${item.unitPrice.toFixed(2)}</td>
          <td style="text-align: right;">\${currency} \${item.lineTotal.toFixed(2)}</td>
        </tr>
      \`).join('')}
    </tbody>
  </table>

  <table class="totals">
    <tr><td>Subtotal:</td><td style="text-align: right;">${currency} ${subtotal.toFixed(2)}</td></tr>
    <tr><td>VAT (${taxRate}%):</td><td style="text-align: right;">${currency} ${taxAmount.toFixed(2)}</td></tr>
    <tr class="total-row"><td>Total Due:</td><td style="text-align: right;">${currency} ${total.toFixed(2)}</td></tr>
  </table>

  <div class="payment-info">
    <strong>Payment Details:</strong><br>
    Bank: ${data.bankName || 'Your Bank'}<br>
    Account: ${data.accountNumber || '000000000'}<br>
    Reference: ${data.invoiceNumber}
  </div>

  <div class="footer">
    Thank you for your business!
  </div>
</body>
</html>
`;

return {
  ...data,
  items: processedItems,
  subtotal: subtotal.toFixed(2),
  taxRate: taxRate,
  taxAmount: taxAmount.toFixed(2),
  total: total.toFixed(2),
  currency: currency,
  invoiceHtml: html
};
