// Generate personalized follow-up email for quote
const data = $input.first().json;
const daysSince = Math.floor((Date.now() - new Date(data.sentDate)) / (1000 * 60 * 60 * 24));

let subject, body;

if (daysSince <= 2) {
  subject = `Following up on your quote ${data.quoteNumber}`;
  body = `
    <p>Hi ${data.customerName.split(' ')[0]},</p>
    <p>I wanted to follow up on the quote I sent you recently. Have you had a chance to review it?</p>
    <p>If you have any questions or would like to discuss any adjustments, I'm happy to jump on a quick call.</p>
    <p>Looking forward to hearing from you!</p>
  `;
} else if (daysSince <= 5) {
  subject = `Quick check-in: Quote ${data.quoteNumber}`;
  body = `
    <p>Hi ${data.customerName.split(' ')[0]},</p>
    <p>Just checking in on the quote I sent over. I know things get busy, but I wanted to make sure it didn't slip through the cracks.</p>
    <p>Is there anything holding you back from moving forward? I'd be happy to address any concerns.</p>
    <p>Let me know either way!</p>
  `;
} else {
  subject = `Last chance: Quote ${data.quoteNumber} expiring soon`;
  body = `
    <p>Hi ${data.customerName.split(' ')[0]},</p>
    <p>I noticed the quote I sent is approaching its expiry date. Before it expires, I wanted to reach out one more time.</p>
    <p>If timing isn't right, just let me know and I can extend the quote or circle back in a few months.</p>
    <p>Otherwise, I'd love to help you get started!</p>
  `;
}

return {
  ...data,
  emailSubject: subject,
  emailBody: body,
  followUpSequence: daysSince <= 2 ? 1 : (daysSince <= 5 ? 2 : 3)
};

// Generate meeting reminder/follow-up
const data = $input.first().json;
const meetingDate = new Date(data.meetingDate);
const now = new Date();
const hoursUntil = (meetingDate - now) / (1000 * 60 * 60);

let subject, body;

if (hoursUntil > 0 && hoursUntil <= 24) {
  // Reminder before meeting
  subject = `Reminder: Our meeting tomorrow`;
  body = `
    <p>Hi ${data.customerName.split(' ')[0]},</p>
    <p>Just a friendly reminder about our meeting scheduled for ${meetingDate.toLocaleString()}.</p>
    <p><strong>Agenda:</strong> ${data.agenda || 'Discussion of your project requirements'}</p>
    <p>Looking forward to speaking with you!</p>
  `;
} else if (hoursUntil < 0 && hoursUntil > -48) {
  // Follow-up after meeting
  subject = `Great speaking with you!`;
  body = `
    <p>Hi ${data.customerName.split(' ')[0]},</p>
    <p>Thank you for taking the time to meet with me. I really enjoyed our conversation!</p>
    <p>As discussed, here's a summary of our next steps:</p>
    <ul>
      <li>I'll send over the proposal by end of week</li>
      <li>Feel free to reach out if you have any questions</li>
    </ul>
    <p>Talk soon!</p>
  `;
}

return {
  ...data,
  emailSubject: subject,
  emailBody: body,
  isReminder: hoursUntil > 0
};

// Follow-up when proposal was viewed
const data = $input.first().json;
const viewedAt = new Date(data.viewedAt);
const hoursSinceView = (Date.now() - viewedAt) / (1000 * 60 * 60);

let subject, body;

if (hoursSinceView <= 4) {
  // Strike while iron is hot
  subject = `Quick question about the proposal`;
  body = `
    <p>Hi ${data.customerName.split(' ')[0]},</p>
    <p>I noticed you just checked out our proposal. Perfect timing!</p>
    <p>Any initial thoughts or questions? I'm around now if you'd like to chat.</p>
  `;
} else {
  subject = `Thoughts on the proposal?`;
  body = `
    <p>Hi ${data.customerName.split(' ')[0]},</p>
    <p>I saw you had a chance to review our proposal. I'd love to hear your thoughts!</p>
    <p>If you'd like to discuss anything or have questions, just reply and we can set up a quick call.</p>
  `;
}

return {
  ...data,
  emailSubject: subject,
  emailBody: body,
  hotLead: hoursSinceView <= 4
};
