// Calculate quote totals
const items = $input.first().json.items || [];

let subtotal = 0;
const processedItems = items.map((item, index) => {
  const qty = parseFloat(item.quantity) || 1;
  const price = parseFloat(item.unitPrice) || 0;
  const lineTotal = qty * price;
  subtotal += lineTotal;

  return {
    lineNumber: index + 1,
    description: item.description || 'Item',
    quantity: qty,
    unitPrice: price,
    lineTotal: lineTotal
  };
});

const taxRate = parseFloat($input.first().json.taxRate) || 15;
const taxAmount = subtotal * (taxRate / 100);
const total = subtotal + taxAmount;
const discountPercent = parseFloat($input.first().json.discount) || 0;
const discountAmount = subtotal * (discountPercent / 100);
const finalTotal = total - discountAmount;

return {
  ...$input.first().json,
  items: processedItems,
  subtotal: subtotal.toFixed(2),
  taxRate: taxRate,
  taxAmount: taxAmount.toFixed(2),
  discountPercent: discountPercent,
  discountAmount: discountAmount.toFixed(2),
  total: finalTotal.toFixed(2),
  currency: $input.first().json.currency || 'ZAR'
};

// Generate HTML quote template
const data = $input.first().json;

const itemsHtml = data.items.map(item => `
  <tr>
    <td>${item.lineNumber}</td>
    <td>${item.description}</td>
    <td style="text-align: right;">${item.quantity}</td>
    <td style="text-align: right;">${data.currency} ${item.unitPrice.toFixed(2)}</td>
    <td style="text-align: right;">${data.currency} ${item.lineTotal.toFixed(2)}</td>
  </tr>
`).join('');

const html = `
<!DOCTYPE html>
<html>
<head>
  <style>
    body { font-family: Arial, sans-serif; margin: 40px; color: #333; }
    .header { display: flex; justify-content: space-between; margin-bottom: 40px; }
    .logo { font-size: 24px; font-weight: bold; color: #007AFF; }
    .quote-info { text-align: right; }
    .quote-number { font-size: 20px; font-weight: bold; }
    .customer { margin-bottom: 30px; }
    table { width: 100%; border-collapse: collapse; margin-bottom: 30px; }
    th { background: #f5f5f5; padding: 12px; text-align: left; border-bottom: 2px solid #ddd; }
    td { padding: 12px; border-bottom: 1px solid #eee; }
    .totals { width: 300px; margin-left: auto; }
    .totals td { padding: 8px 12px; }
    .totals .total-row { font-weight: bold; font-size: 18px; background: #f5f5f5; }
    .footer { margin-top: 50px; padding-top: 20px; border-top: 1px solid #ddd; font-size: 12px; color: #666; }
    .terms { margin-top: 30px; padding: 20px; background: #f9f9f9; border-radius: 8px; }
  </style>
</head>
<body>
  <div class="header">
    <div class="logo">${data.businessName || 'Your Company'}</div>
    <div class="quote-info">
      <div class="quote-number">${data.quoteNumber}</div>
      <div>Date: ${new Date(data.createdDate).toLocaleDateString()}</div>
      <div>Valid Until: ${new Date(data.validUntil).toLocaleDateString()}</div>
    </div>
  </div>

  <div class="customer">
    <strong>Quote For:</strong><br>
    ${data.customerName || 'Customer'}<br>
    ${data.customerEmail || ''}<br>
    ${data.customerPhone || ''}
  </div>

  <table>
    <thead>
      <tr>
        <th>#</th>
        <th>Description</th>
        <th style="text-align: right;">Qty</th>
        <th style="text-align: right;">Unit Price</th>
        <th style="text-align: right;">Total</th>
      </tr>
    </thead>
    <tbody>
      ${itemsHtml}
    </tbody>
  </table>

  <table class="totals">
    <tr><td>Subtotal:</td><td style="text-align: right;">${data.currency} ${data.subtotal}</td></tr>
    ${data.discountPercent > 0 ? `<tr><td>Discount (${data.discountPercent}%):</td><td style="text-align: right;">-${data.currency} ${data.discountAmount}</td></tr>` : ''}
    <tr><td>VAT (${data.taxRate}%):</td><td style="text-align: right;">${data.currency} ${data.taxAmount}</td></tr>
    <tr class="total-row"><td>Total:</td><td style="text-align: right;">${data.currency} ${data.total}</td></tr>
  </table>

  <div class="terms">
    <strong>Terms & Conditions:</strong><br>
    ${data.terms || '• Quote valid for 30 days\n• 50% deposit required to commence\n• Balance due on completion'}
  </div>

  <div class="footer">
    Thank you for your business!
  </div>
</body>
</html>
`;

return {
  ...data,
  quoteHtml: html
};
